mapper.select:

SELECT br_id, br_name, br_region, br_addr, br_tel, br_site, br_info, br_type, br_image, br_thumb, br_program, br_time, br_price, TO_CHAR(br_date, 'YYYY-MM-DD') AS br_date
FROM (
        SELECT /*+ INDEX_DESC(brewery brewery_pk) */
            rownum AS rnum, br_id, br_name, br_region, br_addr, br_tel, br_site, br_info, br_type, br_image, br_thumb, br_program, br_time, br_price, br_date
        FROM brewery
        WHERE rownum <= #{pageNum} * #{amount}
    ) brewerylist
WHERE 



 rnum > (#{pageNum} - 1) * #{amount}

mapper.insert:
/* Brewery - breweryInsert */
select brewery_seq.nextval from dual
INSERT INTO brewery(br_id, br_name, br_region, br_addr, br_tel, br_site, br_info, br_type, br_program, br_time, br_price) VALUES(#{br_id}, #{br_name}, #{br_region}, #{br_addr}, #{br_tel}, #{br_site}, #{br_info}, #{br_type}, #{br_program}, #{br_time}, #{br_price})

mapper.select:
/* Brewery - breweryDetail */
SELECT br_id, br_name, br_region, br_addr, br_tel, br_site, br_info, br_type, br_image, br_program, br_time, br_price, 
to_char(br_date, 'YYYY-MM-DD') as br_date, br_image, br_thumb
FROM brewery
WHERE br_id = #{br_id}

mapper.update:
/* Brewery - breweryUpdate */ UPDATE brewery SET br_name = #{br_name}, br_region = #{br_region}, br_addr = #{br_addr}, br_tel = #{br_tel}, br_site = #{br_site}, br_info = #{br_info}, br_type = #{br_type}, br_program = #{br_program}, br_time = #{br_time}, br_price = #{br_price}, br_date = sysdate
, br_thumb = #{br_thumb}
, br_image = #{br_image}
WHERE br_id = #{br_id}
