mapper.select:
/* Product - productDetail */
SELECT pd_id, br_id, pd_name, pd_price, pd_sort, pd_degree, pd_volume, pd_info, to_char(pd_date, 'YYYY-MM-DD') as pd_date
FROM Product
WHERE pd_id = #{pd_id}

mapper.insert:
/* Product - productInsert */
select product_seq.nextval from dual
INSERT INTO product(pd_id, pd_name, pr_price, pd_sort, pd_degree, pd_volume, pd_info, pd_thumb, pd_image) VALUES(#{pd_id}, #{pd_name}, #{pd_pirce}, #{pd_sort}, #{pd_degree}, #{pd_volume}, #{pd_info}, #{pd_thumb}, #{pd_image})
