mapper.update:
/* Qna - readCntUpdate */
UPDATE qna	
SET qna_readcnt = qna_readcnt + 1
WHERE qna_no = #{qna_no}

mapper.insert:
/* Qna - qnaInsert */
select qna_seq.nextval from dual
INSERT INTO qna(qna_no, user_no, qna_title, qna_content, qna_thumb, qna_file) VALUES(#{qna_no}, 1, #{qna_title}, #{qna_content}, #{qna_thumb}, #{qna_file})

mapper.update:
/* Qna - qnaUpdate */
UPDATE qna
SET qna_title = #{qna_title},
	qna_content = #{qna_content},
	qna_date = sysdate

		, qna_thumb = #{qna_thumb}


		, qna_file = #{qna_file}

WHERE qna_no = #{qna_no}
